INSERT INTO library_author VALUES(1,'اریک','فروم','1900-03-23','اریش فروم در فرانکفورت آم ماین آلمان به دنیا آمد. تحصیلات خود را در دانشگاه‌های هیدلبرگ و مونیخ و انستیتو روانکاوی برلین ادامه داد. بعد از ظهور نازیسم در آلمان به ژنو رفت و سرانجام در سال ۱۹۳۴ به آمریکا مهاجرت کرد و تابعیت این کشور را پذیرفت و در دانشگاه کلمبیای نیویورک مشغول به فعالیت شد، سپس به مکزیک رفت و در دانشگاه مستقل ملی مکزیک کرسی روانکاوی را تأسیس کرد. وی در انتهای عمر خویش به سوئیس بازگشت و در ۱۹۸۰ درگذشت.',0);
INSERT INTO library_author VALUES(2,'مل','رابینز','1968-10-06',replace(replace('مل رابینز (Mel Robbins) در کانزاس سیتی آمریکا چشم به جهان گشود. او دوران کودکی سخت و پرتنشی را سپری کرد. وی بعد از اتمام تحصیلات مقدماتی خود، به عنوان دانشجوی حقوق وارد دانشگاه شد. رابینز پس از فارغ‌التحصیلی و کسب مدرک رسمی، در مشاغل مختلفی تجربه کسب کرد. اولین کار حرفه‌ای مل رابینز، حضور به عنوان کارشناس حقوقی در شبکه‌ی سی‌اِن‌اِن بود. وی در سال 1994، در مقام وکیل مدافع جنایی در شهر نیویورک مشغول به کار شد.\r\n\r\nمل رابینز در سال 2011، پس از سال‌ها فعالیت در مشاغلی از قبیل مجری‌گری در تلویزیون، وکالت و سخنوری، با نگارش کتاب «دیگر نگو حالت خوب است» به دنیای نویسندگی وارد شد. شش سال پس از انتشار این کتاب، یکی از محبوب‌ترین آثار مل رابینز با عنوان «قانون 5 ثانیه» به چاپ رسید. این اثر از سوی مخاطبان کتاب‌های خودسازی، با استقبال خیره‌کننده‌ای مواجه شد و شهرتی بین‌المللی برای رابینز به ارمغان آورد.\r\n\r\nمل رابینز در  28سالگی، با کارآفرینی آمریکایی به نام کریستوفر رابینز ازدواج کرد. حاصل زندگی مشترک آن‌ها سه فرزند است.','\r',char(13)),'\n',char(10)),1);
INSERT INTO library_author VALUES(3,'مسعود','نیکوکار','1953-08-27',replace(replace('دکتر مسعود نیکوکار\r\n«مسعود نیکوکار» در سال 1332 در کاشان متولد شد. وی در ابتدا پنج جلد کتاب نوشت که در انتشارات آزاده به چاپ رسیدند و پس از مشاهده استقبال بسیار خوب دانشجویان و مدرسین از این کتاب‌ها، در سال 1379، انتشارات گسترش علوم پایه را تاسیس نمود.','\r',char(13)),'\n',char(10)),1);
