INSERT INTO library_book VALUES(1,'هنر عشق ورزیدن',50000,replace(replace('درباره کتاب\r\n\r\nخلاصه کتاب هنر عشق ورزیدن از اریک فروم، عشق رو به‌عنوان نوعی هنر تعریف می‌کنه. نویسنده معتقده که برای فراگیری هنر عشق ورزیدن، مثل هر هنر دیگه‌ای باید تمرین کنیم تا به مهارت برسیم. خوندنِ این کتاب نه‌فقط برای زوج‌ها، بلکه برای آدم‌های مجرد هم که توی روابط عاشقانه نیستن، مفید واقع میشه. این کتاب رو باید هر انسانی بخونه تا مسئله‌ی وجود انسان رو درک کنه، تا عشق پدر و مادر نسبت‌به فرزند رو بفهمه و درباره‌ی عشق به معشوق و عشق به خود بینش تازه‌ای به‌دست بیاره.\r\n\r\nتوی این کتاب، نویسنده سعی می‌کنه تا ابتدا با تشریح انواع مختلف عشق، مفهوم عشق رو برای خواننده واضح‌تر کنه و بعد درباره‌ی راه‌های عشق‌ورزیدنِ صحیح صحبت کنه. علاوه‌بر این، اریک فروم معتقده که همه‌ی تلاش‌های ما برای عشق‌ورزیدن محکوم به شکست هست، مگر اینکه با جد برای تکامل همه‌جانبه‌ی شخصیت خودمون تلاش کنیم تا درنهایت به جهت‌بینی سازنده‌ای برسیم.\r\n\r\nنکته‌ی جالب درباره‌ی کتاب اینه که به عقیده‌ی نویسنده، دنیای سرمایه‌داریِ امروز، مفهوم عشق رو به قهقرا برده و از اون یه کالای مبادلاتی ساخته.\r\nجملات کتاب هنر عشق ورزیدن:\r\n\r\n    اگه کسی به ما بگه که عاشق گل هست؛ اما درعوض، اغلب اوقات یادش بره که به گل‌هاش آب بده، عشقش رو به گل‌ها باور نمی‌کنیم. عشق واقعی، رغبت جدی به زندگی و پرورش چیزی هست که عمیقا بهش مهر می‌ورزیم. زمانی که رغب جدی وجود نداشته باشه، بنابراین عشقی هم وجود نداره.\r\n\r\n    ****\r\n\r\n    مشکل اکثر مردم اینه که در وهله‌ی اول انتظار دارن که دیگران اون‌ها رو دوست داشته باشن و خودشون تلاشی نمی‌کنن تا دیگران رو دوست داشته باشن.\r\n\r\n    ****\r\n\r\n    عاشقِ کسی بودن فقط یه احساس شدید نیست، بلکه یک تصمیمه، قضاوته و قول هست. اگه عشق فقط یه احساس بود، دیگه پایداری در این قول که هم‌دیگه رو تا ابد دوست خواهیم داشت، مفهوم پیدا نمی‌کرد.\r\n\r\n    ****\r\n\r\n    عشق نابالغ اینطور هست: «من عاشقتم، چون بهت نیاز دارم.» عشق بالغ اینطور هست: «من بهت نیاز دارم، چون عاشقت هستم.»\r\n\r\n    ****\r\n\r\n    مردم فکر می‌کنن که عاشق‌شدن یه اتفاق آسون هست؛ اما پیداکردن چیزی که حقیقتا عاشقش باشیم و از جانبش عشق بگیریم، اتفاق دشواری هست.\r\n\r\n    ****\r\n\r\n    گرفتنِ عشق از دیگری، به ما قدرت میده و دادنِ عشق به دیگری به ما شجاعت رو هدیه می‌کنه.\r\n\r\nاگه دوست داری این کتاب رو بخونی، اما به‌هر‌دلیلی، فرصت کافی نداری، خبر خوب اینه که چکیدا صفر تا صد این کتاب رو برات خلاصه کرده و همین الان می‌تونی خلاصه کتاب هنر عشق ورزیدن رو ازطریق سایت یا اپلیکیشن چکیدا مطالعه کنی.\r\nدر چه صورتی از این کتاب لذت می‌بری؟\r\n- اگه تازه ازدواج کردی و می‌خوای یه زندگی عاشقانه بسازی\r\n- اگه دوست داری مفهوم عشق رو بهتر درک کنی\r\n- اگه توی زندگی مشترک به مشکل خوردی و راه ترمیم رابطه‌ت رو بلد نیستی','\r',char(13)),'\n',char(10)),1,'1967-08-27',1);
INSERT INTO library_book VALUES(2,'قانون 5 ثانیه',160000,replace(replace('ملانیا رابینز نویسنده‌ی کتاب قانون پنج ثانیه که به مل راینز نیز معروف است\r\n\r\nوی می‌گوید قانون ۵ ثانیه وقتی به دادم رسید که زندگی‌ام از هر نظر رو به نابودی بود. کتاب قانون ۵ ثانیه تنها کتاب پرفروش و پرطرفدار او نیست. اولین کتاب او «گفتن من خوبم را متوقف کن» است. وی در سال 2014 موفق شد جایزه‌ای تحت عنوان «Gracie Award» دریافت کند. این جایزه در انجمن زنان آمریکا در حوزه رسانه آماده شده است. ملانیا رابینز در مورد کتاب قانون ۵ ثانیه می‌گوید:\r\n\r\n    قانون ۵ ثانیه نابغه، رهبر، ستاره‌ی موسیقی، ورزشکار، هنرمند و خلاصه عامل تغییر درون شما را بیدار می‌کند.\r\nنگاهی کلی بر کتاب قانون ۵ ثانیه\r\n\r\nدر جای جای کتاب قانون ۵ ثانیه می‌توانید کلمه شجاعت را ببینید. نویسنده از مخاطب خودش می‌‎خواهد که برای تغییر در زندگی شجاعت به خرج بدهد. او می‌گوید:\r\n\r\n    می‌توانید شجاعت یا راحتی را انتخاب کنید، اما نمی‌توانید هر دو را داشته باشید.\r\n\r\nاگر به این جمله فکر کنید، خواهید دانست که ملانیا رابینز از مخاطب می‌خواهد که برای رسیدن به موفقیت و تحقق بخشیدن به رویاهای خودش، باید از ناحیه راحتی خارج شود و شجاعت وارد شدن به دایره ترس‌ها را داشته باشد.\r\n\r\nدنیا به کسانی پاداش می‌دهد و به کسانی کمک می‌کنند که رشد و پیشرفت کنند که این شجاعت را داشته باشند که دل را به دریا بزنند و شروع کنند و از شکست هیچ ترس و هراسی نداشته باشند. قانون پنج ثانیه یک قانون بسیار ساده و ابتدایی است که می‌گوید:\r\n\r\n    اگر یک ایده به ذهنتان خطور کرد که با آن ایده می‌توانید به اهداف و رویاهای خود برسید، تنها پنج ثانیه فرصت دارید که به صورت فیزیکی کاری برای آن ایده انجام دهید. اگر پنج ثانیه گذشت و از نظر فیزیکی کاری برای آن ایده انجام ندادید، مغزتان به صورت خودکار آن ایده را از بین خواهد برد.\r\n\r\nالبته دلیل اینکه مغز ایده‌ای که ظرف ۵ ثانیه برای آن کاری انجام ندهید را از بین می‌برد هم مشخص است. مغز برای حفاظت و نگهداری از ما کاری می‌کند که کارهای پرزحمت و پردردسر را انجام ندهیم و خودمان را به زحمت و دردسر نیندازیم. مغز از ما می‌خواهد که همیشه در همین وضعیت کنونی باقی بمانیم و رشد و پیشرفت نکنیم و خود را در وضعیت خطر قرار ندهیم. به همین خاطر است که وقتی یک ایده جدید به ذهنمان می‌رسد مغز خیلی سریع می‌گوید: اگر نتوانستی چه می‌شود؟ با ورود به این کار ممکن است آسیب ببینی! حال اگر ظرف ۵ ثانیه از نظر فیزیکی کاری برای ایده خود انجام دهیم، قدرت منفی‌گرایی مغز کمتر می‌شود.\r\n\r\nدر کتاب قانون پنج ثانیه از مخاطب خواسته می‌شود که وقتی به کاری فکر می‌کنید، ایده‌ای به ذهنتان می‌رسد و یا برای انجام یک کار اشتیاق پیدا می‌کنید، خیلی سریع قانون پنج ثانیه را به کار ببندید. به این صورت که از پنج تا یک را به صورت معکوس بشمارید و خیلی سریع برای ایده یا کار مورد علاقه خودتان کاری انجام دهید. مهم نیست که این کار کوچک یا بزرگ باشد. حتی اگر یک یادداشت یا برنامه‌ریزی ساده هم انجام دهید، یک گام مهم در جهت رسیدن به موفقیت برداشته‌اید. اگر این کار را انجام دهید مطمئن باشید که ذهنتان روی ایده‌ای که در ذهن دارید متمرکز می‌شود و از افکار منفی و ناامیدکننده دور خواهد شد.','\r',char(13)),'\n',char(10)),2,'2022-01-27',8);
